import React, { useEffect, useState } from "react";
import axios from "axios";

const useAxios = (url) => {
    const [response, setResponse] = useState(null)

    useEffect(() => {
        const getData = async () => {
            const res = await axios.get(url);
            const json = await res.json();
            setResponse(json);
        }
        getData();
    }, []);

    return { response };
}

export default useAxios;
